policy_content.body:
permit(
    principal is Jans::Workload,
    action in [Jans::Action::"Update"],
    resource is Jans::Issue
)when{
    principal.org_id == resource.org_id
};

permit(
    principal is Jans::User,
    action in [Jans::Action::"Update"],
    resource is Jans::Issue
)when{
    principal.country == resource.country
};

schema.body:
namespace Jans {
  type Url = {"host": String, "path": String, "protocol": String};
  entity Access_token = {"aud": String, "exp": Long, "iat": Long, "iss": TrustedIssuer, "jti": String};
  entity Issue = {"country": String, "org_id": String};
  entity Role;
  entity TrustedIssuer = {"issuer_entity_id": Url};
  entity User in [Role] = {"country": String, "email": String, "sub": String, "username": String};
  entity Workload = {"client_id": String, "name": String, "org_id": String};
  entity id_token = {"acr": String, "amr": String, "aud": String, "exp": Long, "iat": Long, "jti": String, "sub": String};
  entity Userinfo_token  = {"iss": String, "jti": String };
  type Context = {
    access_token: Access_token,
    id_token: id_token,
    userinfo_token: Userinfo_token,
  };
  action "Update" appliesTo {
    principal: [Workload, User, Role],
    resource: [Issue],
    context: Context
  };
}
